import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../Style/Card.scss";
// import loadingImage from "../images/loading.gif";

function Card(props) {
  const [imageLoading, setImageLoading] = useState(true);
  const data = props.data;

  return (
    <>
      <div className="rounded border mb-3 col-sm-12 col-md-6 col-lg-4 col-xl-3  p-2">
        <div
          className="position-relative border-card"
          style={{ height: "350px" }}
        >
          {imageLoading && (
            <img
              className="image rounded"
              style={{ objectFit: "cover", height: "100%" }}
              src={loadingImage}
              alt="Loading..."
            />
          )}
          <img
            className={`image rounded ${imageLoading ? "d-none" : ""}`}
            style={{ objectFit: "cover", height: "100%" }}
            src={data.imageUrl}
            alt={data.name}
            onLoad={() => setImageLoading(false)}
          />
          <div className=" position-absolute top-50 start-50  middle">
            <h3 className="text-light mb-3">{data.name}</h3>
            <p>{data.generalInformation}</p>
            <h6 className="text-light text-uppercase pb-2">${data.price}</h6>
            <Link to={`/${data._id}`} className="btn btn-outline-light">
              More
            </Link>
          </div>
        </div>
        <hr />
        <h6 className="text-center h5 position-relative">{data.name}</h6>
      </div>
    </>
  );
}

export default Card;
