import { createSlice } from "@reduxjs/toolkit";
const setRestaurantSlice = createSlice({
    name: "restaurant",
    initialState: {array:[],sortArray:[]},
    reducers: {
        setRestaurant: (state,actions) => {
            state.array=actions.payload
        },
        setSortRestaurant: (state,actions) => {
            state.sortArray=actions.payload.sort((a,b) =>b.price-a.price);
        }

    }
})
export const { setRestaurant,setSortRestaurant } = setRestaurantSlice.actions;
export default setRestaurantSlice.reducer;
