import React, { useState } from 'react'

function AdminLogin() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const toggleShowPassword = () => setShowPassword(!showPassword);
    // Formni joylashtirish funksiyasi
    const handleSubmit = e => {
        e.preventDefault();
        // Username va passwordni serverga yuborish
        fetch('/admin/login', {
            method: 'POST',
            body: JSON.stringify({ username, password }),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                if (response.ok) {
                    // Agar kirish muvaffaqiyatli bo'lsa, sahifani yuklash
                    window.location.href = '/admin/dashboard';
                } else {
                    alert('Login muvaffaqiyatsiz bo\'ldi!');
                }
            })
            .catch(error => {
                console.error('Error:', error);
            });
    };

    return (
        <>
            <div className="container" style={{ marginTop: '80px' }}>
                <div className="row">
                    <div className="col-xl-3"></div>
                    <div className="col-xl-6">
                        <h4 className='text-center'>Log in</h4>
                        <form action="" class="row g-4">
                            <div class="col-12">
                                <label>Username<span class="text-danger">*</span></label>
                                <div class="input-group">
                                    <div class="input-group-text"><i class='bx bxs-user fs-4'></i></div>
                                    <input type="text" class="form-control" placeholder="Enter Username" />
                                </div>
                            </div>
                            <div class="col-12">
                                <label>Password<span class="text-danger">*</span></label>
                                <div class="input-group">
                                    <div class="input-group-text"><i class='bx bxs-lock-alt fs-4'></i></div>
                                    <input type={showPassword ? "text" : "password"} id="password" class="form-control col-11" placeholder="Enter Password" />
                                    <input type="checkbox" checked={showPassword} class="form-control" style={{width:'10px'}} onChange={toggleShowPassword} />
                                </div>
                            </div>

                            <div class="col-sm-6">
                                <div class="form-check">
                                    <input class="form-check-input" type="checkbox" id="inlineFormCheck" />
                                    <label class="form-check-label" for="inlineFormCheck">Remember me</label>
                                </div>
                            </div>

                            <div class="col-sm-6">
                                <a href="/" class="float-end text-primary">Forgot Password?</a>
                            </div>

                            <div class="col-12">
                                <button type="submit" class="btn btn-outline-secondary px-4 float-end mt-4">login</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default AdminLogin
