import React, { useEffect, useState } from 'react'
import Card from '../component/Card'
import Carusel from "../component/Carusel";
import restaurant, { setRestaurant,setSortRestaurant } from '../redux/reducers/restaurant.js';
import { useSelector, useDispatch } from 'react-redux'
import axios from 'axios'
function Home() {
  const [load, setLoad] = useState(false)
  const dispatch = useDispatch();
  const restaurants = useSelector(state => state.restaurant.array);
  // const sortRestaurants = useSelector(state => state.restaurant.sortArray)
  // console.log(sortRestaurants)
  // console.log(load&&restaurants)
  // const sortRestaurants = load&&restaurants?.sort((a,b)=>b.price-a.price);
  // console.log(sortRestaurants)
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleInputChange = (event) => {
    setQuery(event.target.value);
    if (event.target.value.length >= 2) {
      const regex = new RegExp(`${event.target.value}`, 'i');
      const newResult = restaurants?.filter((weddinghall) => regex.test(weddinghall.name));
      console.log(newResult)
      setResults(newResult);
    } else {
      setResults([]);
    }
  };
  useEffect(() => {
    axios
      .get('http://localhost:5000/restaurant')
      .then(res => {
        // console.log(res.data);
        dispatch(setRestaurant(res.data))
        setLoad(true)
      })
    return () => {
      setLoad(false)
    }
  }, [])
  return (
    <>{
      load ? <>
        <div className="container-fluid mt-5">
          <Carusel />
        </div>
        <div className="container">
        <input className="form-control me-2 m-2" value={query} onChange={handleInputChange}  type="search" placeholder="Search" aria-label="Search" />
        {/* <input type="text" /> */}
        </div>
        <div className="container">
          <div className="row  mt-4 ">
            {
             results.length===0?restaurants.map((restaurant) => {
                return (
                  <Card key={restaurant._id} data={restaurant} />
                )
              }):
              results.map((restaurant) => {
                return (
                  <Card key={restaurant._id} data={restaurant} />
                )
              })
            }
          </div>
        </div>
      </> :
        <h1 style={{ height: "80vh" }} className='w-100'>
          <i style={{ position: "fixed", top: "45vh", left: "50vw", zIndex: 12, fontSize: "100px" }} className='bx bx-loader bx-spin bx-flip-horizontal' ></i>
        </h1>
    }
    </>
  )
}

export default Home
