import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'
function Restaurant() {
    const [data, setData] = useState()
    const [load, setLoad] = useState(false)
    const { _id } = useParams()
    console.log(_id)
    console.log(data)
    useEffect(() => {
        axios
            .get(`http://localhost:5000/restaurant/${_id}`)
            .then((response) => {
                setData(response.data)
                setLoad(false)
            })
            .catch((error) => {
                console.log(error);
            })
        return () => {
            setLoad(true)
        }
    }, [])
    return (
        <>
            <div className="container" style={{marginTop:'80px'}}>
                <h4 className='text-center'>{data?.name}</h4>
                <div className="row">
                    <div className="col-xl-6">
                        <img className='w-100 rounded' style={{height:'500px',objectFit:'cover'}} src={data?.imageUrl} alt="404" />
                    </div>
                    <div className="col-xl-6 ">
                    <iframe src="https://lms.tuit.uz" title="W3Schools Free Online Web Tutorials"></iframe>
                    <h1 className='fw-bold'></h1>
                    </div>
                </div>
            </div> 

        
        </>
    )
}

export default Restaurant
